package LeetCode567;

import java.util.*;

public class Solution {
    boolean isMatch(Map<Character, Integer> s1Map, Map<Character, Integer> s2Map) {
        for (char c : s1Map.keySet()) {
            if (!s1Map.get(c).equals(s2Map.getOrDefault(c, 0)))
                return false;
        }
        return true;
    }

    public boolean checkInclusion(String s1, String s2) {
        Map<Character, Integer> s1Map = new HashMap<>();
        Map<Character, Integer> s2Map = new HashMap<>();
        for (int i = 0; i < s1.length(); i++) {
            s1Map.put(s1.charAt(i), s1Map.getOrDefault(s1.charAt(i), 0) + 1);
        }
        for (int left = 0, right = s1.length() - 1; right < s2.length(); left++, right++) {
            if (left == 0) {
                for (int i = 0; i < s1.length(); i++) {
                    s2Map.put(s2.charAt(i), s2Map.getOrDefault(s2.charAt(i), 0) + 1);
                }
            } else {
                s2Map.put(s2.charAt(left - 1), s2Map.get(s2.charAt(left - 1)) - 1);
                s2Map.put(s2.charAt(right), s2Map.getOrDefault(s2.charAt(right), 0) + 1);
            }
            if (isMatch(s1Map, s2Map))
                return true;
        }
        return false;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.checkInclusion("", ""));
    }
}
